// parallaxSketch.js
export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.3;
    let welcomeMessage = "Welcome to SenpAI Suggests";

    p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight);
        // Create background layers (pink circles)
        for (let i = 0; i < 15; i++) {
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(60, 150),
                speed: speed + i * 0.03
            });
        }
        p.textAlign(p.CENTER, p.CENTER); // Align the text to the center
    };

    p.draw = () => {
        p.clear();
        p.noStroke();

        // Parallax background bubbles
        layers.forEach(layer => {
            p.fill(255, 108, 180, 120); // Pink color
            p.circle(layer.x, layer.y, layer.size);
            layer.x += layer.speed;

            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });

        // Parallax "Welcome to SenpAI Suggests"
        p.fill(255, 105, 180); // Light pink color for better contrast
        p.textSize(150); // Make the text larger to match the logo style
        p.textFont('animeFont, sans-serif'); // Use the custom font for the logo
        let x = p.width / 2; // Center text horizontally
        let y = p.height / 2; // Center text vertically

        // Adding text shadow for better readability
        p.textStyle(p.BOLD);
        p.textAlign(p.CENTER, p.CENTER);
        p.text(welcomeMessage, x, y);
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight);
    };
};
