import React, { useState } from 'react';
import './likeAnimesPage.css';

const LikedAnimesPage = ({ likedAnimes }) => {
  // State to keep track of which anime descriptions are open
  const [openDescriptions, setOpenDescriptions] = useState({});

  // Toggle function to open or close description for each anime
  const toggleDescription = (id) => {
    setOpenDescriptions((prevState) => ({
      ...prevState,
      [id]: !prevState[id]
    }));
  };

  return (
    <div className="liked-animes-page">
      <h1>Liked Animes</h1>
      <ul className="anime-list">
        {likedAnimes.map((anime) => (
          <li key={anime.id} className="anime-item" onClick={() => toggleDescription(anime.id)}>
            <h3>{anime.title}</h3>
            {/* Show description only if the item is toggled open */}
            {openDescriptions[anime.id] && <p>{anime.description}</p>}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LikedAnimesPage;
