export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.3;

    p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight);
        for (let i = 0; i < 20; i++) { // Increased number of bubbles for more coverage
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(60, 150),
                speed: speed + i * 0.03
            });
        }
    };

    p.draw = () => {
        p.clear(); // Clears previous frame for smooth animation
        p.noStroke();

        layers.forEach(layer => {
            p.fill(255, 108, 180, 120); 
            p.circle(layer.x, layer.y, layer.size);
            layer.x += layer.speed;

            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight);
    };
};
