// mainMenu.jsx
import React, { useEffect, useState } from 'react';
import p5 from 'p5';
import { parallaxSketch } from './parallaxSketch';
import styles from './mainMenu.module.css';

function MainMenu() {
    const [scrollY, setScrollY] = useState(0);

    useEffect(() => {
        const sketch = new p5(parallaxSketch);
        return () => sketch.remove();
    }, []);

    useEffect(() => {
        const handleScroll = () => {
            setScrollY(window.scrollY);
        };
        window.addEventListener('scroll', handleScroll);
        return () => window.removeEventListener('scroll', handleScroll);
    }, []);

    // Adjust opacity based on scrollY value (fade out as user scrolls)
    const welcomeOpacity = Math.max(1 - scrollY / 200, 0);

    return (
        <div className={styles.mainMenuContainer}>
            <div className={styles.parallaxCanvas}></div>
            <div className={styles.header}>
                <h1 className={styles.title}>Senp<span id="highlight">ai</span> Suggests</h1>
                <h2
                    className={styles.welcomeMessage}
                    style={{ opacity: welcomeOpacity }}
                >
                    Welcome to SenpAI Suggests!
                </h2>
                <nav className={styles.navOptions}>
                    <a href="/likes" className={styles.navLink}>My Likes</a>
                    <a href="/wishlist" className={styles.navLink}>My Wishlist</a>
                    <a href="/feeling-lucky" className={styles.navLink}>I'm Feeling Lucky</a>
                    <a href="/animelist" className={styles.navLink}>Search</a>
                </nav>
            </div>
        </div>
    );
}

export default MainMenu;
