import React, { useState, useEffect } from "react";
import styles from './mainMenu.module.css'; // Import your CSS Module styles

function MainMenu() {
  const [offsetY, setOffsetY] = useState(0);

  // Function to handle scroll event and update offsetY
  const handleScroll = () => setOffsetY(window.pageYOffset);

  useEffect(() => {
    // Add scroll event listener
    window.addEventListener("scroll", handleScroll);

    // Cleanup scroll event listener
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <section className={styles.parallax}>
      {/* Parallax Background Layers */}
      <div
        className={styles.parallaxBackground}
        style={{ transform: `translateY(-${offsetY * 0.5}px)` }} // Moves slower than scroll
      />
      <div
        className={styles.parallaxTriangles}
        style={{ transform: `translateY(${offsetY * 0.8}px)` }} // Moves faster than scroll
      />

      {/* Main Menu Content */}
      <div className={styles.content}>
        <header className={styles.header}>
          <h1 className={styles.logo}>Senp<span id="highlight">ai</span> Suggests</h1>
        </header>

        <div className={styles.menuOptions}>
          <button className={`${styles.menuButton} ${styles.showOnScroll}`} style={{ animationDelay: "0s" }}>My Likes</button>
          <button className={`${styles.menuButton} ${styles.showOnScroll}`} style={{ animationDelay: "0.2s" }}>My Watchlist</button>
          <button className={`${styles.menuButton} ${styles.showOnScroll}`} style={{ animationDelay: "0.4s" }}>My Wishlist</button>
          <button className={`${styles.menuButton} ${styles.showOnScroll}`} style={{ animationDelay: "0.6s" }}>I'm Feeling Lucky</button>
          <button className={`${styles.menuButton} ${styles.showOnScroll}`} style={{ animationDelay: "0.8s" }}>Search</button>
          <button className={`${styles.menuButton} ${styles.showOnScroll}`} style={{ animationDelay: "1s" }}>About Us</button>
        </div>

        {/* Welcome Message */}
        <div className={styles.welcomeOverlay}>
          <h1 className={styles.welcomeText}>Welcome to Senp<span id="highlight">ai</span> Suggests!</h1>
        </div>
      </div>
    </section>
  );
}

export default MainMenu;
