// mainMenu.jsx
import React, { useEffect, useState } from 'react';
import p5 from 'p5';
import { parallaxSketch } from './parallaxSketch';
import { Link } from 'react-router-dom';
import styles from './mainMenu.module.css';

function MainMenu() {
    const [showWelcome, setShowWelcome] = useState(true);

    useEffect(() => {
        const sketch = new p5(parallaxSketch);
        setTimeout(() => setShowWelcome(false), 3000); // Hide welcome message after 3 seconds
        return () => sketch.remove();
    }, []);

    return (
        <div className={styles.mainMenuContainer}>
            <div className={styles.parallaxCanvas}></div> {/* Canvas for parallax */}
            {showWelcome && (
                <div className={styles.welcomeOverlay}>
                    <h1 className={styles.welcomeText}>Welcome to Senp<span id="highlight">AI</span> Suggests!</h1>
                </div>
            )}
            <div className={styles.header}>
                <h1 className={styles.title}>Senp<span id="highlight">AI</span> Suggests</h1>
                {/* No navigation links for now */}
            </div>
        </div>
    );
}

export default MainMenu;
