import p5 from 'p5';

export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.2;
    let animeFont;

    p.preload = () => {
        animeFont = p.loadFont('/animeFont.ttf');
    };

    p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight);
        for (let i = 0; i < 10; i++) {
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(50, 200),
                speed: speed + i * 0.05
            });
        }
    };

    p.draw = () => {
        p.background(0, 0, 0);
        p.noStroke();

        // Parallax effect
        layers.forEach(layer => {
            p.fill(255, 108, 180, 150);
            p.circle(layer.x, layer.y, layer.size);
            layer.x += layer.speed;

            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });

        // Set the anime font
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(72);
        p.fill(255, 105, 180);
        p.textFont(animeFont);
        p.text("Welcome to Senpai Suggests", p.width / 2, p.height / 2);

        // Text for explaining the system (placed on left)
        if (p.mouseY > p.height / 2) {
            p.textSize(32);
            p.fill(255, 255, 255);
            p.textAlign(p.LEFT, p.CENTER);
            p.text("An anime recommendation system that caters to your suggestions with the guidance of your Senpai.", p.width * 0.1, p.height * 0.75);
        }

        // Text for explaining the system (placed on right)
        if (p.mouseY > p.height * 0.75) {
            p.textSize(32);
            p.fill(255, 255, 255);
            p.textAlign(p.RIGHT, p.CENTER);
            p.text("With the help of Jikan API and Gemini AI, it uses your suggestions to allow your Senpai to give you the wisdom in navigating through the art of anime.", p.width * 0.9, p.height * 0.85);
        }

        // Show sections as user scrolls
        // My Likes Section
        if (p.mouseY > p.height * 1.1) {
            p.textSize(48);
            p.textAlign(p.CENTER, p.CENTER);
            p.fill(255, 255, 255);
            p.text("My Likes", p.width / 2, p.height * 1.2);
        }

        // My Wishlist Section
        if (p.mouseY > p.height * 1.4) {
            p.textSize(48);
            p.textAlign(p.CENTER, p.CENTER);
            p.fill(255, 255, 255);
            p.text("My Wishlist", p.width / 2, p.height * 1.5);
        }

        // I'm Feeling Lucky Section
        if (p.mouseY > p.height * 1.7) {
            p.textSize(48);
            p.textAlign(p.CENTER, p.CENTER);
            p.fill(255, 255, 255);
            p.text("I'm Feeling Lucky", p.width / 2, p.height * 1.8);
        }

        // Ask SenpAI Section
        if (p.mouseY > p.height * 2) {
            p.textSize(48);
            p.textAlign(p.CENTER, p.CENTER);
            p.fill(255, 255, 255);
            p.text("Ask SenpAI", p.width / 2, p.height * 2.1);
        }

        // About Us Section
        if (p.mouseY > p.height * 2.3) {
            p.textSize(48);
            p.textAlign(p.CENTER, p.CENTER);
            p.fill(255, 255, 255);
            p.text("About Us", p.width / 2, p.height * 2.4);
        }
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight);
    };
};
