export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.3;

    p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight); // Ensure canvas size covers the full page
        for (let i = 0; i < 20; i++) { // More bubbles for coverage
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(60, 150),
                speed: speed + i * 0.03
            });
        }
    };

    p.draw = () => {
        p.clear(); // Clears the previous frame for smooth animation
        p.noStroke();

        // Get scroll position for parallax effect
        const scrollPosition = p.mouseY; // You can use p.window.scrollY for page scroll

        layers.forEach(layer => {
            p.fill(255, 108, 180, 120); // Bubble color
            // Adjust the vertical position based on scroll to create the parallax effect
            layer.y += scrollPosition * layer.speed * 0.01;

            // Reset position when bubbles go off-screen
            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }

            if (layer.y > p.height) {
                layer.y = -layer.size; // Reset bubble position if it goes off-screen vertically
            }

            p.circle(layer.x, layer.y, layer.size);
        });
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight); // Adjust canvas size on window resize
    };
};
