import React, { useEffect, useState } from 'react';
import p5 from 'p5';
import { parallaxSketch } from './parallaxSketch';
import styles from './mainMenu.module.css';

function MainMenu() {
    const [scrollPosition, setScrollPosition] = useState(0);

    const handleScroll = () => {
        setScrollPosition(window.scrollY);
    };

    useEffect(() => {
        window.addEventListener('scroll', handleScroll);
        const sketch = new p5(parallaxSketch);
        return () => {
            window.removeEventListener('scroll', handleScroll);
            sketch.remove();
        };
    }, []);

    return (
        <div className={styles.mainMenuContainer}>
            <div className={styles.parallaxCanvas}></div> {/* Parallax background */}

            <header className={styles.header}>
                <h1 className={styles.logo}>Senp<span id="highlight">ai</span> Suggests</h1>
            </header>

            <div className={styles.welcomeOverlay}>
                <h1 className={styles.welcomeText}>Welcome to Senp<span id="highlight">ai</span> Suggests!</h1>
            </div>
        </div>
    );
}

export default MainMenu;
