import './AnimeItemGrid.css';

// should take in props so it can be customized for different uses
export default function AnimeItemGrid(listOfAnimesIDs) {

  if (!Array.isArray(listOfAnimesIDs)) {
    throw new Error("Prop to AnimeItemGrid was expected to be an array.");
  }

  return (
    /*<div
      style={{
        display: 'flex',
        gap: '10px'
      }}
    >
      {listOfTiles}
    </div>*/
    <div className='anime-item-grid'>
      {listOfAnimesIDs.map((anime, index) => (
        <div key={index} className="anime-item">
            <img src={anime.imageUrl} alt={anime.title} />
            <div>
                <h3>{anime.title}</h3>
                <p>{anime.description || "No description available."}</p>
            </div>
        </div>
      ))}
    </div>
  )
}
