import p5 from 'p5';

export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.2;
    let animeFont;

    // Preload the font
    p.preload = () => {
        animeFont = p.loadFont('/animeFont.ttf'); // Replace with actual font path
    };

    p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight);
        for (let i = 0; i < 10; i++) {
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(50, 200),
                speed: speed + i * 0.05
            });
        }
    };

    p.draw = () => {
        p.background(255, 255, 255);  // Keep original background color
        p.noStroke();

        // Parallax effect
        layers.forEach(layer => {
            p.fill(255, 108, 180, 150); // Light pink color with transparency
            p.circle(layer.x, layer.y, layer.size);
            layer.x += layer.speed;

            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });

        // Set the anime font
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(85); // Set font size
        p.fill(255, 105, 180); // Text color (light pink)
        p.textFont(animeFont); // Apply the anime font
        p.text("Welcome to Senpai Suggests", p.width / 2, p.height / 2);
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight);
    };
};
