// importing the React logo as placeholder image
import placeholderImage from './logo192.png'

// Replace below function bodies w/ the API Search-by-ID functionality when you get the chance. :)
//  (though if you could keep the functions as returning objects that would be sick,
//   but if not that's cool too)

const getAnimeByID = ( ID ) => {
  return {
    title: "Placeholder Title",
    image: placeholderImage
    /* other attributes */
  }
}

const getAnimeByName = (name) => {
  let title;
  let image;

  if (name) {
    title = "Placeholder Title";
    image = placeholderImage;
  }

  return {
    title,
    image,
    /* other attributes */
  }
}

/* other getAnimeBy___ object declarations */

export { getAnimeByID, getAnimeByName /* other getAnimeBy___ objects */ };
