import React from "react";
import { Parallax, ParallaxLayer } from "@react-spring/parallax";
import { Link } from "react-scroll";
import styles from "./mainMenu.module.css";

function MainMenu() {
  return (
    <Parallax pages={3}>
      {/* Background Layer */}
      <ParallaxLayer offset={0} speed={0.2} className={styles.background}>
        <img src="/assets/background.png" alt="Background" />
      </ParallaxLayer>

      {/* Midground Layer */}
      <ParallaxLayer offset={0} speed={0.5} className={styles.midground}>
        <img src="/assets/midground.png" alt="Midground" />
      </ParallaxLayer>

      {/* Foreground Layer */}
      <ParallaxLayer offset={0} speed={0.8} className={styles.foreground}>
        <img src="/assets/cherryBlossomForeground.png" alt="Foreground" />
      </ParallaxLayer>

      {/* Main Content */}
      <ParallaxLayer offset={0} speed={1} className={styles.content}>
        <h1 className={styles.title}>Senpai Suggests</h1>
        <nav className={styles.nav}>
          <Link to="instructions" smooth={true} duration={500} className={styles.link}>
            Learn More
          </Link>
        </nav>
      </ParallaxLayer>

      {/* Instructions Section */}
      <ParallaxLayer offset={1} speed={0.4} className={styles.instructions}>
        <h2>Welcome to Senpai Suggests</h2>
        <p>Discover anime recommendations tailored to your tastes. Use the main menu to navigate through your likes, wishlist, and more!</p>
      </ParallaxLayer>
    </Parallax>
  );
}

export default MainMenu;
