import p5 from 'p5';

export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.2;
    let animeFont;

    p.preload = () => {
        animeFont = p.loadFont('/animeFont.ttf');
    };

    p.setup = () => {
        p.createCanvas(p.windowWidth, 3000); // Height adjusted for scrolling effect
        for (let i = 0; i < 10; i++) {
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(50, 200),
                speed: speed + i * 0.05
            });
        }
    };

    p.draw = () => {
        p.background(0, 0, 0); // Keep background dark
        p.noStroke();

        // Move bubbles and handle parallax
        layers.forEach(layer => {
            p.fill(255, 108, 180, 150);
            p.circle(layer.x, layer.y, layer.size);

            // Move the bubble in the x-direction
            layer.x += layer.speed;

            // If bubble moves off screen, wrap it to the other side
            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });

        // Set the anime font
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(72);
        p.fill(255, 105, 180);
        p.textFont(animeFont);
        p.text("Welcome to Senpai Suggests", p.width / 2, p.height / 4);

        // "My Likes" section after scrolling
        if (p.window.scrollY > p.height / 2) {
            p.textSize(48);
            p.text("My Likes", p.width / 2, p.height * 0.75);
            p.textSize(24);
            p.text("This will take you to the liked animes that you chose!", p.width / 2, p.height * 0.85);
        }

        p.redraw();  // Ensures continuous redrawing
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, 3000);
    };
};
