import React, { useEffect } from 'react';
import './mainMenu.css';

function MainMenu() {
  useEffect(() => {
    const parallaxElements = document.querySelectorAll('.parallaxLayer');

    const handleScroll = () => {
      const scrollY = window.scrollY;
      parallaxElements.forEach((layer, index) => {
        const speed = (index + 1) * 0.2; // Adjust the speed for each layer
        const offset = scrollY * speed;
        layer.style.transform = `translateY(${offset}px)`;
      });
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="mainMenuContainer">
      <div className="parallaxLayer layer1"></div>
      <div className="parallaxLayer layer2"></div>
      <div className="parallaxLayer layer3"></div>
      <div className="content">
        <header className="header">
          <h1 className="logo">Senp<span id="highlight">ai</span> Suggests</h1>
        </header>
        <div className="welcomeOverlay">
          <h1 className="welcomeText">Welcome to Senp<span id="highlight">ai</span> Suggests!</h1>
        </div>
      </div>
    </div>
  );
}

export default MainMenu;
