import React, { useRef } from 'react';
import { Parallax, ParallaxLayer } from '@react-spring/parallax';
import styles from './mainMenu.module.css';
import moon from './moon.png'; // Replace with your image path
import land from './land.png'; // Replace with your image path
import cat from './cat.gif'; // Replace with your logo path

function MainMenu() {
  const ref = useRef();

  return (
    <div className={styles.pageContainer}>
      <Parallax pages={4} ref={ref}>
        {/* Background layers for parallax effect */}
        <ParallaxLayer
          offset={0}
          speed={0.5}
          style={{
            backgroundImage: `url(${moon})`,
            backgroundSize: 'cover',
          }}
        />

        <ParallaxLayer
          offset={1.5}
          speed={0.3}
          style={{
            backgroundImage: `url(${land})`,
            backgroundSize: 'cover',
          }}
        />

        {/* Logo and Welcome Message */}
        <ParallaxLayer offset={0.2} speed={0.2} className={styles.logoContainer}>
          <img src={cat} alt="Logo" className={styles.logo} />
          <h1 className={styles.header}>Welcome to Senpai Suggests</h1>
        </ParallaxLayer>

        {/* Parallax Layers for each button */}
        <ParallaxLayer offset={0.8} speed={0.4}>
          <button className={styles.parallaxButton} onClick={() => ref.current.scrollTo(1)}>
            My Likes
          </button>
        </ParallaxLayer>

        <ParallaxLayer offset={1.2} speed={0.4}>
          <button className={styles.parallaxButton} onClick={() => ref.current.scrollTo(2)}>
            My Watchlist
          </button>
        </ParallaxLayer>

        <ParallaxLayer offset={1.6} speed={0.4}>
          <button className={styles.parallaxButton} onClick={() => ref.current.scrollTo(3)}>
            My Wishlist
          </button>
        </ParallaxLayer>

        <ParallaxLayer offset={2.0} speed={0.4}>
          <button className={styles.parallaxButton} onClick={() => ref.current.scrollTo(4)}>
            Iâ€™m Feeling Lucky
          </button>
        </ParallaxLayer>

        <ParallaxLayer offset={2.4} speed={0.4}>
          <button className={styles.parallaxButton} onClick={() => ref.current.scrollTo(5)}>
            Search
          </button>
        </ParallaxLayer>

        <ParallaxLayer offset={2.8} speed={0.4}>
          <button className={styles.parallaxButton} onClick={() => ref.current.scrollTo(0)}>
            About Us
          </button>
        </ParallaxLayer>
      </Parallax>
    </div>
  );
}

export default MainMenu;
