import p5 from 'p5';

export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.2;
    let animeFont;

    p.preload = () => {
        animeFont = p.loadFont('/animeFont.ttf');
    };

    p.setup = () => {
        p.createCanvas(p.windowWidth, p.windowHeight * 3); // Slightly extended for scrolling without hiding initial text
        for (let i = 0; i < 10; i++) {
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(50, 200),
                speed: speed + i * 0.05
            });
        }
    };

    p.draw = () => {
        p.background(0, 0, 0);
        p.noStroke();

        // Parallax bubbles
        layers.forEach(layer => {
            p.fill(255, 108, 180, 150);
            p.circle(layer.x, layer.y, layer.size);
            layer.x += layer.speed;
            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });

        // Welcome message at top
        p.textAlign(p.CENTER, p.CENTER);
        p.textFont(animeFont);
        p.textSize(72);
        p.fill(255, 105, 180);
        p.text("Welcome to Senpai Suggests", p.width / 2, p.height / 8);

        // Sections for scroll
        const sections = [
            { text: "My Likes", desc: "This will take you to the liked animes that you chose!", y: p.height * 1.0 },
            { text: "My Wishlist", desc: "Add animes to your wishlist!", y: p.height * 1.5 },
            { text: "I'm Feeling Lucky", desc: "Let Senpai suggest a random anime!", y: p.height * 2.0 },
            { text: "Ask SenpAI", desc: "Get anime advice from SenpAI!", y: p.height * 2.5 },
            { text: "About Us", desc: "Learn more about our team.", y: p.height * 3.0 }
        ];

        // Display each section when in view
        sections.forEach((section) => {
            if (window.scrollY > section.y - p.height / 2) {
                p.textSize(48);
                p.text(section.text, p.width / 2, section.y);
                p.textSize(24);
                p.text(section.desc, p.width / 2, section.y + 30);
            }
        });
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, p.windowHeight * 3);
    };
};
