import { BrowserRouter as Router, Route, Routes, Navigate, useLocation } from 'react-router-dom';
import Login from './pages/loginPage';
import MainMenu from './pages/mainMenu';
import Navbar from './Components/Navbar.jsx';
import Signup from './pages/Signup';
import AnimeList from './pages/animelist';
import LikedAnimesPage from './pages/LikedAnimePage.jsx';
import PageNotFound from './pages/PageNotFound.jsx';
import WishListPage from './pages/WishListPage.jsx';
import AboutUs from './pages/AboutUs';

const AppFunctions = () => {

}

const App = () => {
  return (
    <Router>
      const currPage = useLocation();
      const navbarIsVisible = currPage.pathname !== '/login' ||
                              currPage.pathname !== '/register' ||
                              currPage.pathname !== '*';
      <>
        {

        }
        {navbarIsVisible && <Navbar />}
        <Routes>
          <Route path="/" element={<Navigate to="/login" />}/>
          <Route path="/login" element={<Login />} />
          <Route path="/register" element={<Signup />} />
          <Route path="/mainmenu" element={<MainMenu />} />
          <Route path="/animelist" element={<AnimeList />} />
          <Route path="/likedanime" element={<LikedAnimesPage />} />
          <Route path="/wishlist" element={<WishListPage />} />
          <Route path="/aboutus" element={<AboutUs />} />
          <Route path="*" element={<PageNotFound />} />
        </Routes>
      </>

    </Router>

  );
};

export default App;
