import p5 from 'p5';

export const parallaxSketch = (p) => {
    let layers = [];
    let speed = 0.2;
    let animeFont;

    p.preload = () => {
        animeFont = p.loadFont('/animeFont.ttf');
    };

    p.setup = () => {
        p.createCanvas(p.windowWidth, 3000); // Extended height for scrollable content
        for (let i = 0; i < 10; i++) {
            layers.push({
                x: p.random(p.width),
                y: p.random(p.height),
                size: p.random(50, 200),
                speed: speed + i * 0.05
            });
        }
    };

    p.draw = () => {
        p.background(0, 0, 0); // Keep background dark
        p.noStroke();

        // Parallax effect: Moving bubbles
        layers.forEach(layer => {
            p.fill(255, 108, 180, 150);
            p.circle(layer.x, layer.y, layer.size);
            layer.x += layer.speed;
            if (layer.x > p.width) {
                layer.x = -layer.size;
                layer.y = p.random(p.height);
            }
        });

        // Set anime font for "Welcome to Senpai Suggests"
        p.textAlign(p.CENTER, p.CENTER);
        p.textSize(72);
        p.fill(255, 105, 180);
        p.textFont(animeFont);
        p.text("Welcome to Senpai Suggests", p.width / 2, p.height / 4);

        // My Likes section after scrolling
        if (p.window.scrollY > p.height * 0.3) {
            p.textSize(48);
            p.text("My Likes", p.width / 2, p.height * 0.6);
            p.textSize(24);
            p.text("This will take you to the liked animes that you chose!", p.width / 2, p.height * 0.65);
        }

        // My Wishlist section after scrolling
        if (p.window.scrollY > p.height * 0.6) {
            p.textSize(48);
            p.text("My Wishlist", p.width / 2, p.height * 0.75);
            p.textSize(24);
            p.text("This is where you can add animes to your wishlist!", p.width / 2, p.height * 0.8);
        }

        // I'm Feeling Lucky section after scrolling
        if (p.window.scrollY > p.height * 0.8) {
            p.textSize(48);
            p.text("I'm Feeling Lucky", p.width / 2, p.height * 0.9);
            p.textSize(24);
            p.text("Let Senpai Suggest a random anime for you!", p.width / 2, p.height * 0.95);
        }

        // Ask SenpAI section after scrolling
        if (p.window.scrollY > p.height * 1.1) {
            p.textSize(48);
            p.text("Ask SenpAI", p.width / 2, p.height * 1.2);
            p.textSize(24);
            p.text("Get advice from SenpAI about your anime choices!", p.width / 2, p.height * 1.25);
        }

        // About Us section after scrolling
        if (p.window.scrollY > p.height * 1.4) {
            p.textSize(48);
            p.text("About Us", p.width / 2, p.height * 1.5);
            p.textSize(24);
            p.text("Learn more about the team behind Senpai Suggests.", p.width / 2, p.height * 1.55);
        }
    };

    p.windowResized = () => {
        p.resizeCanvas(p.windowWidth, 3000); // Resizes canvas as window resizes
    };
};
