import React, { useEffect } from 'react';
import './mainMenu.module.css';

function MainMenu() {
    useEffect(() => {
        // JavaScript for the parallax effect on scroll
        const parallax = () => {
            const layers = document.querySelectorAll('.layer');
            const scrollY = window.scrollY;
            layers.forEach((layer, index) => {
                const speed = 0.5 + index * 0.1; // Adjust speed for each layer
                layer.style.transform = `translateY(${scrollY * speed}px)`;
            });
        };

        window.addEventListener('scroll', parallax);
        return () => window.removeEventListener('scroll', parallax);
    }, []);

    return (
        <div className="mainMenu">
            {/* Parallax Background Layers */}
            <div className="parallax-container">
                <div className="layer background"></div>
                <div className="layer midground"></div>
                <div className="layer foreground"></div>
            </div>

            {/* Logo/Header Section */}
            <header className="header">
                <h1 className="logo">Senp<span id="highlight">ai</span> Suggests</h1>
            </header>

            {/* Welcome Message */}
            <div className="welcomeOverlay">
                <h1 className="welcomeText">Welcome to Senp<span id="highlight">ai</span> Suggests!</h1>
            </div>
        </div>
    );
}

export default MainMenu;
