import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import p5 from 'p5';
import { parallaxSketch } from './parallaxSketch';
import styles from './mainMenu.module.css';

function MainMenu() {
    useEffect(() => {
        const sketch = new p5(parallaxSketch);
        return () => sketch.remove();
    }, []);

    return (
        <div className={styles.mainMenuContainer}>
            <div className={styles.parallaxCanvas}></div> {/* Canvas for parallax */}

            <div className={styles.header}>
                <h1 className={styles.title}>Welcome to Senpai Suggests</h1> {/* Anime font styled */}
                <p className={styles.welcomeMessage}>Discover your next favorite anime with personalized recommendations!</p>
                <div className={styles.navOptions}>
                    <Link to="instructions" smooth={true} duration={500} className={styles.navLink}>Learn More</Link>
                </div>
            </div>
        </div>
    );
}

export default MainMenu;
